mapper.select:
SELECT io_num, m.ma_num, m.ma_name, c.clt_num, c.clt_name, i.io_cnt, i.io_unit, m.ma_price, i.io_date, i.io_state, i.rec_date, i.emp_id FROM in_order i JOIN material m ON i.ma_id = m.ma_id JOIN client c ON i.clt_id = c.clt_id
io_state = #{io_state}
AND ma_name like CONCAT('%', #{ma_name}, '%')
AND i.io_date >= #{istartDate}
AND #{iendDate} >= DATE(i.io_date)
AND i.rec_date >= #{rstartDate}
AND #{rendDate} >= DATE(i.rec_date)

mapper.update:
UPDATE in_order
SET io_cnt = #{io_cnt}, io_state = #{io_state}, rec_date = #{rec_date} 
WHERE io_num = #{io_num}
