mapper.select:
SELECT io_id, io_num, m.ma_num, m.ma_name, c.clt_num, c.clt_name, i.io_cnt, i.io_unit, m.ma_price, i.io_date, i.io_state, i.rec_date, i.emp_id, i.io_reg_date, i.io_update_date, i.update_emp_id, CASE WHEN i.update_emp_id IS NULL THEN (SELECT emp_name FROM employee WHERE emp_id = i.emp_id) ELSE (SELECT emp_name FROM employee WHERE emp_id = i.update_emp_id) END AS emp_name FROM in_order i JOIN employee e ON i.emp_id = e.emp_id JOIN material m ON i.ma_id = m.ma_id JOIN client c ON i.clt_id = c.clt_id
io_state = #{io_state}
AND ma_name like CONCAT('%', #{ma_name}, '%')
AND i.io_date >= #{istartDate}
AND #{iendDate} >= DATE(i.io_date)
AND i.rec_date >= #{rstartDate}
AND #{rendDate} >= DATE(i.rec_date)
ORDER BY io_id DESC LIMIT #{pageVO.startPage}, #{pageVO.pageSize}

mapper.update:
UPDATE in_order
SET io_cnt = #{io_cnt}, io_state = #{io_state}, rec_date = #{rec_date}, 
io_amount = #{io_cnt}*#{ma_price}, io_update_date = now(), update_emp_id = #{update_emp_id} 
WHERE io_num = #{io_num}

mapper.select:
SELECT count(io_id) FROM in_order i JOIN employee e ON i.emp_id = e.emp_id JOIN material m ON i.ma_id = m.ma_id JOIN client c ON i.clt_id = c.clt_id
io_state = #{io_state}
AND ma_name like CONCAT('%', #{ma_name}, '%')
AND i.io_date >= #{istartDate}
AND #{iendDate} >= DATE(i.io_date)
AND i.rec_date >= #{rstartDate}
AND #{rendDate} >= DATE(i.rec_date)
