mapper.select:
SELECT oo_num, emp_name, clt_num, clt_name, pro_num, pro_name, oo_cnt, oo_start_date, oo_end_date, oo_state FROM out_order join employee e on out_order.emp_id = e.emp_id join client c on out_order.clt_id = c.clt_id join product p on out_order.pro_id = p.pro_id
clt_name LIKE CONCAT('%', #{ovo.clt_name}, '%')
AND emp_name LIKE CONCAT('%', #{ovo.emp_name}, '%')
AND oo_start_date >= #{ovo.oo_start_date_1}
AND #{ovo.oo_start_date_2} >= DATE(oo_start_date)
AND oo_end_date >= #{ovo.oo_end_date_1}
AND #{ovo.oo_end_date_2} >= DATE(oo_end_date)
LIMIT #{pvo.startPage}, #{pvo.pageSize}

mapper.update:
UPDATE out_order SET update_emp_id = #{update_emp_id}, oo_cnt = #{oo_cnt}, oo_state = #{oo_state},
oo_start_date = CASE WHEN #{oo_start_date} = '' THEN NULL ELSE #{oo_start_date} END,
oo_end_date = CASE WHEN #{oo_end_date} = '' THEN NULL ELSE #{oo_end_date} END,
oo_update_date = CURRENT_TIMESTAMP,
clt_cost = #{clt_cost}
WHERE oo_num = #{oo_num}

mapper.select:
SELECT oo_num, out_order.emp_id, update_emp_id, clt_num, clt_name, pro_num, pro_name, oo_cnt, oo_start_date, oo_end_date, oo_state, pro_price, clt_cost, oo_id, CASE WHEN update_emp_id IS NULL THEN (SELECT emp_name FROM employee WHERE emp_id = out_order.emp_id) ELSE (SELECT emp_name FROM employee WHERE emp_id = out_order.update_emp_id) END AS emp_name,
CASE WHEN oo_update_date IS NULL THEN oo_reg_date ELSE oo_update_date END AS oo_reg_date
FROM out_order join client c on out_order.clt_id = c.clt_id join product p on out_order.pro_id = p.pro_id WHERE oo_num = #{oo_num}
