mapper.select:
SELECT wi.work_id, wi.pro_id, wi.oo_id, work_num, line_num, pro_num, pro_name, wi.work_state, oo_cnt, wi.reg_date, wi.update_date, wi.work_cnt, oo_num, oo_end_date, wi.emp_id, wi.update_emp_id, emp_name FROM work_instr wi LEFT JOIN out_order oo ON wi.oo_id = oo.oo_id LEFT JOIN client clt ON oo.clt_id = clt.clt_id LEFT JOIN line l ON wi.line_id = l.line_id LEFT JOIN product pro ON wi.pro_id = pro.pro_id LEFT JOIN employee emp ON wi.emp_id = emp.emp_id
work_state = #{work_state}
AND wi.pro_id = (SELECT pro.pro_id FROM product pro WHERE pro.pro_num = #{pro_num})
AND wi.reg_date >= #{startDate}
AND #{endDate} >= DATE(wi.reg_date)

mapper.update:
UPDATE work_instr SET work_cnt = #{work_cnt}, line_id = #{line_id}, work_state = #{work_state}, 
	update_emp_id = #{emp_id}, update_date = now() 
WHERE work_id = #{work_id}
